/**
  ** Function: btmtk_profile_register
  **
  ** Discription: register profile external message handler, handle message from mtkbt
  **
  **/
void btmtk_profile_register(int profileId, void *message_handler, void *pContext);

/**
  ** Function: btmtk_profile_unregister
  **
  ** Discription: unregister profile external message handler,
  **
  **/
void btmtk_profile_unregister(int profileId);

/**
  ** Function: btmtk_clear_handlers
  **
  ** Discription: unregister all profile external message handler,
  **
  **/
void btmtk_clear_handlers();

/**
  ** Function: btmtk_sendmsg
  **
  ** Discription: profile send message to mtkbt
  **
  **/
kal_bool btmtk_sendmsg(int profile_id, void* ptr, int size);

/**
  ** Function: btmtk_sendevt
  **
  ** Discription: profile send event to btif_task
  **
  **/
kal_bool btmtk_sendevt(int profile_id, void* ptr, int size);

/**
  ** Function: btmtk_get_remote_device_name
  **
  **
  ** Discription:for headset use
  **/
kal_uint16 btmtk_get_remote_device_name(bt_addr_struct *remAddr, char *name, kal_uint16 len);


