/* Value of service class ID is a data element sequence of UUIDs */
const U8 ZeroServClassIdVal[SDP_ZERO_SERV_CLASS_ID_SIZE] = 
{
    DETD_SEQ + DESD_ADD_8BITS,  /* Header is type and size index */
    3,                          /* Number of bytes in the list */

    /* This is a list of one element of type UUID */
    DETD_UUID + DESD_2BYTES,    /* Header of UUID and size of 2 bytes */
    0x10, 0x00  /* SC_SERVICE_DISCOVERY_SERVER UUID 
                   in Big Endian */
};

/* Version Number List is a Data Element Sequence 0x0200 */
const U8 ZeroVersionNumberList[SDP_ZERO_VER_NUMBER_SIZE] = 
{
    DETD_SEQ + DESD_ADD_8BITS,
    0x06,

    DETD_UINT + DESD_2BYTES,
    0x01, 0x00, /* Version 1.0 */

    DETD_UINT + DESD_2BYTES,
    0x01, 0x01  /* Version 1.1 */
};

